package Lec1;

public class Assginment_Op_Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 8;
		x += 3;// x=x+3;
		System.out.println(x);
		int a = 7;
		a -= 5;// a=a-5;
		System.out.println(a);

		a *= 6;//a=a*6
		System.out.println(a);
		
		
		
		x=9;
		int y = ++x + x++;   
		System.out.println(y);
		System.out.println(x);
		

	}

}
